abstract contract DelegatedTaskAcceptorV1 is TaskAcceptorV1, TaskAcceptorV1Proxy {
    constructor(address _upstream) TaskAcceptorV1Proxy(_upstream) {
        return;
    }

    function _acceptTaskResults(
        uint256[] calldata,
        bytes calldata,
